import { registerUser } from "../services/internalApiService"
import { useNavigate } from 'react-router-dom'
import { useState } from 'react'
import Menu from "./Menu"
const Register = () => {
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    const [errors, setErrors] = useState([])
    const [currentUser, setCurrentUser] = useState({
        username: "",
        id: "",
        score: 0,
    })
    const navigate = useNavigate()
    const handleSubmit = (e) => {
        e.preventDefault()

        const newUser = {
            username: username,
            password: password
        }
        registerUser(newUser)
            .then(data => {
                if(data.user){
                    localStorage.setItem()
                }

            })
            .catch(err => {
                const errRes = err.response.data.errrors
                console.log(err.response);
                const errorArr = []
                for(const key of Object.keys(errRes)){
                    errorArr.push(errRes[key].message)
                }
                setErrors(errorArr)
            })
    }

    return (
        <>
            {
                currentUser.id.length > 0 ? 
                <Menu currentUser={currentUser}/>
                :
                <>
                <div className="container">
                <form action="" onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="input" className="form-label">Username: </label>
                    </div>
                    {
                        errors.map((err, i) => {
                            return <p className="text-danger" key={i}>{err}</p> 
                        })
                    }
                    <input type="text" className="form-control" onChange={(e) => setUsername(e.target.value)} value={username} />
                    <div className="mb-3">
                        <label htmlFor="input" className="form-label">Password: </label>
                    </div>
                    {
                        errors.map((err, i) => {
                            return <p className="text-danger" key={i}>{err}</p> 
                        })
                    }
                    <input type="password" className="form-control" onChange={(e) => setPassword(e.target.value)} value={password} />
                    <button type="submit" className="btn btn-primary">Register</button>
                </form>
            </div>
            </>
            }
        </>
    )

}
export default Register
