import React, { useState, useEffect } from "react"
import Help from "./Help"
import { Link, useNavigate } from 'react-router-dom'
import MenuButtonSVG from "./MenuButtonSVG"
import './styles/Menu.css'
import jwt_decode from 'jwt-decode';


const Menu = () => {

    const [startHelp, setStartHelp] = useState(false)

    const [currentUser, setCurrentUser] = useState({
    })
    const navigate = useNavigate()
    useEffect(() => {
        const token = localStorage.getItem('token')
        if(token) {
            const user = jwt_decode(token)
            console.log(user);
            if(!user){
                localStorage.removeItem('token')
                navigate('/login')
            } else{
                console.log(user);
                setCurrentUser(currentUser => ({ username: user.username , id: user.id, score: user.score }))
            }
        }
    }, [])
    return (
        <>
        {
            startHelp ? 
            <Help />
            :
            ''
        }
            <div className="title">
                <img src={require('../codecrackdownlogo.png')} alt="" className="img-fluid banner-img"></img>
                <div className="circle">
                <div className="fa fa-unlock spinningKey"></div>
                </div>
                <h2 className="info">USERNAME: {currentUser.username}</h2>
                <h2 className="info">SCORE: {currentUser.score}</h2>
                <Link id="start" className="menu-btn" to="/game" state={{ currentUser }}> 
                    <span>PLAY</span>
                    <MenuButtonSVG />
                </Link>
                <button id="shop" className="menu-btn" onClick={() => console.log("hi")}>
                    <span>SHOP</span>
                    <MenuButtonSVG />
                </button>
                <button id="help" className="menu-btn" onClick={() => setStartHelp(true)}>
                    <span>HELP</span>
                    <MenuButtonSVG />
                </button>
                {
                    currentUser.username ?
                    <button id="login" className="menu-btn" onClick={() => navigate('/login')}>
                        <span>LOGOUT</span> 
                        <MenuButtonSVG />
                    </button>
                    :
                    <button id="login" className="menu-btn" onClick={() => navigate('/login')}>
                        <span>LOGIN</span> 
                        <MenuButtonSVG />
                    </button>
                }
            </div>
        </>
    )

}
export default Menu
