import { registerUser } from "../services/internalApiService"
import { useNavigate } from 'react-router-dom'
import { useState } from 'react'
import { validateUser } from "../services/validations"

const Register = () => {
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    const [errors, setErrors] = useState({
        usernameError: "",
        passwordError: ""
    })
    const navigate = useNavigate()
    const handleSubmit = (e) => {
        e.preventDefault()

        const newUser = {
            username: username,
            password: password
        }
        setErrors(validateUser(newUser))
        if(errors.usernameError === "" && errors.passwordError === ""){
            registerUser(newUser)
            .then(data => {
                    console.log(data)
                    localStorage.setItem('token', data.user)
                    navigate('/login')
            })
            .catch(err => {
                console.log(err);
            })
        }

    }

    return (
        <>
            <div className="container">
                <form action="" onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="input" className="form-label" style={{fontSize: "32px", color: "white"}}>Username: </label>
                    </div>
                    {
                        errors.usernameError && (
                            <p className="text-danger mt-3">{errors.usernameError}</p> 
                        )
                    }
                    <input type="text" className="form-control" onChange={(e) => setUsername(e.target.value)} value={username} />
                    <div className="mb-3">
                        <label htmlFor="input" className="form-label" style={{fontSize: "32px", color: "white"}}>Password: </label>
                    </div>
                    {
                        errors.passwordError && (
                            <p className="text-danger mt-3">{errors.passwordError}</p> 
                        )
                    }
                    <input type="password" className="form-control" onChange={(e) => setPassword(e.target.value)} value={password} />

                    <button type="submit" className="btn btn-primary">Register</button>
                </form>
            </div>
        </>
    )

}
export default Register
